SELECT r.requiredId,r.userId,r.username,r.userphone,r.zoneId,r.houseTypeId typeId, r.budget,r.budgetBak,r.designType,r.designTypeBak,r.designStyle,r.designStyleBak ,r.houseStatus,r.houseStatusBak,r.fileTime,r.nextCallTime, r.houseDes,r.isNew,r.houseInfo,r.isReady,r.readyDate,r.houseLocation,r.designTime, r.phoneTime,r.customerTips,r.houseTypeIdBak typeIdBak,r.zoneIdBak,r.houseDesBak, r.isNewBak, r.houseInfoBak,r.isReadyBak,r.houseLocationBak,r.designTimeBak,r.phoneTimeBak,r.readyDateBak, r.customerTipsBak,r.createDate,r.callbackTips,r.serviceTips,r.status,r.operatorLog, r.createUserId,r.serviceUserId, zs.name zonename,ht.name typename, ht.price, zsb.name zonenameBak,htb.name typenameBak, ca.nickname,sa.nickname nickname_s FROM `require` r left join zone_set zs on zs.zoneId=r.zoneId left join house_type ht on ht.typeId=r.houseTypeId left join admin_user ca on ca.adminUserId=r.createUserId left join admin_user sa on sa.adminUserId=r.serviceUserId
left join zone_set zsb on zsb.zoneId=r.zoneIdBak left join house_type htb on htb.typeId=r.houseTypeIdBak
where 1=1
and r.requiredId=#{require.requiredId}
and r.userphone=#{require.userphone}

select r.requiredId,r.createDate,r.userId,r.userphone,r.username,r.serviceDate,
r.budget,r.budgetBak,r.houseInfo,r.fileTime,r.nextCallTime,r.callbackTips,
r.status,r.operatorLog,zs.zoneId,zs.name zonename,
(select count(*) from `order` o where o.requiredId=r.requiredId) orderCount ,
(select count(*) from `order` o where o.requiredId=r.requiredId and o.status=1) acceptNum,
(select count(*) from `order` o where o.requiredId=r.requiredId and o.status=1 and o.isSuccess=1) successNum
,rg.giftId,rg.address,rg.status status_g
from `require` r left join 
zone_set zs on zs.zoneId=r.zoneId
left join require_gift rg on r.requiredId=rg.requiredId
 where r.status>4

and r.serviceUserId=#{page.param.serviceUserId}


and (select count(*) from `order` o where o.requiredId=r.requiredId and o.status=1)>0


and r.userId=#{page.param.userId}


and r.status=#{page.param.status}


and r.username like '%${page.param.username}%'


and r.userphone like '${page.param.userphone}%'


and r.createDate > #{page.param.startDate}


and r.createDate < #{page.param.endDate}


and r.serviceDate > #{page.param.serviceStartDate}


and r.serviceDate < #{page.param.serviceEndDate}



and r.callbackTips like '%${page.param.callbackTips}%'


and r.fileTime > #{page.param.startFileTime}


and r.fileTime < #{page.param.endFileTime}



and r.nextCallTime > #{page.param.startNextCallTime}


and r.nextCallTime < #{page.param.endNextCallTime}

 order by r.createDate desc
limit ${page.pageSize*(page.pageIndex-1)},${page.pageSize}

